olute;\r\n\t\tclear:both;\r\n\t}\r\n\r\n\t.scrollable .items div\r\n\t{\r\n\t\tfloat:left;\r\n\t\twidth:663px;\r\n\t}\r\n\r\n\t/* single scrollable item */\r\n\t.scrollable img\r\n\t{\r\n\t\tfloat:left;\r\n\t\tmargin: 10px 11px 10px 10px;\r\n\t\tbackground-color:#fff;\r\n\t\tpadding:2px;\r\n\t\tborder:1px solid #ccc;\r\n\t\twidth: 105px;\r\n\t\theight: 155px;\r\n\t\t-moz-border-radius:4px;\r\n\t\t-webkit-border-radius:4px;\r\n\t}\r\n\r\n\t.vertical img\r\n\t{\r\n\t\tmargin: 10px 6px;\r\n\t}\r\n\r\n\t/* active item */\r\n\t.scrollable .active\r\n\t{\r\n\t\tborder:2px solid #000;\r\n\t\tposition:relative;\r\n\t\tcursor:default;\r\n\t}\r\n\r\n\t/* this makes it possible to add next button beside scrollable */\r\n\t.scrollable\r\n\t{\r\n\t\tfloat:left;\r\n\t}\r\n\r\n\t/* prev, next, prevPage and nextPage buttons */\r\n\ta.browse\r\n\t{\r\n\t\tbackground:url(\"./../images/jqueryTools/hori_large.png\") no-repeat;\r\n\t\tdisplay:none;\r\n\t\twidth:30px;\r\n\t\theight:30px;\r\n\t\tfloat:left;\r\n\t\tmargin:40px 10px;\r\n\t\tcursor:pointer;\r\n\t\tfont-size:1px;\r\n\t}\r\n\r\n\t/* right */\r\n\ta.right \t\t\t\t{ background-position: 0 -30px; clear:right; margin-right: 0px;}\r\n\ta.right:hover \t\t{ background-position:-30px -30px; }\r\n\ta.right:active \t{ background-position:-60px -30px; }\r\n\r\n\r\n\t/* left */\r\n\ta.left\t\t\t\t{ margin-left: 0px; }\r\n\ta.left:hover  \t\t{ background-position:-30px 0; }\r\n\ta.left:active  \t{ background-position:-60px 0; }\r\n\r\n\t/* up and down */\r\n\ta.up, a.down\r\n\t{\r\n\t\tbackground:url(\"./../images/jqueryTools/vert_large.png\") no-repeat;\r\n\t\tfloat: none;\r\n\t\tmargin: 10px 50px;\r\n\t}\r\n\r\n\t/* up */\r\n\ta.up:hover  \t\t{ background-position:-30px 0; }\r\n\ta.up:active  \t\t{ background-position:-60px 0; }\r\n\r\n\t/* down */\r\n\ta.down \t\t\t\t{ background-position: 0 -30px; }\r\n\ta.down:hover  \t\t{ background-position:-30px -30px; }\r\n\ta.down:active  \t{ background-position:-60px -30px; }\r\n\r\n\r\n\t/* disabled navigational button */\r\n\ta.disabled\r\n\t{\r\n\t\tvisibility:hidden !important;\r\n\t}\r\n\t/* SCROLLABLE */\r\n\t\r\n\t/* CSS3 Focus Effects for Input and Textarea */\r\n\tinput, textarea\r\n\t{\r\n\t  -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);\r\n\t  -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);\r\n\t  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);\r\n\t  -webkit-transition: border linear 0.2s, box-shadow linear 0.2s;\r\n\t  -moz-transition: border linear 0.2s, box-shadow linear 0.2s;\r\n\t  -ms-transition: border linear 0.2s, box-shadow linear 0.2s;\r\n\t  -o-transition: border linear 0.2s, box-shadow linear 0.2s;\r\n\t  transition: border linear 0.2s, box-shadow linear 0.2s;\r\n\t}\r\n\r\n\tinput:focus, textarea:focus\r\n\t{\r\n\t  border-color: rgba(82, 168, 236, 0.8);\r\n\t  -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);\r\n\t  -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);\r\n\t  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);\r\n\t  outline: 0;\r\n\t  outline: thin dotted \\9;\r\n\t  /* IE6-9 */\r\n\t}\r\n\r\n\tinput[type=\"file\"]:focus,\r\n\tinput[type=\"radio\"]:focus,\r\n\tinput[type=\"checkbox\"]:focus,\r\n\tselect:focus\r\n\t{\r\n\t  -webkit-box-shadow: none;\r\n\t  -moz-box-shadow: none;\r\n\t  box-shadow: none;\r\n\t  outline: thin dotted #333;\r\n\t  outline: 5px auto -webkit-focus-ring-color;\r\n\t  outline-offset: -2px;\r\n\t}\r\n\r\n\t.ui-sorttable{list-style-type: none; margin: 0; padding: 0;}\r\n\t.ui-sorttable li { margin: 3px; padding: 5px; font-size: 1.2em; height: 1.5em; float: left; width: 250px; font-size: 11px; cursor: move;}\r\n\t.ui-sorttable li input{vertical-align: middle;}\r\n\thtml>body .ui-sorttable li { height: 1.5em; line-height: 1.2em; }\r\n\t.ui-state-highlight { height: 1.5em; line-height: 1.2em; }\r\n\t\r\n\t.ui-autocomplete\r\n\t{\r\n\t\tmax-height: 500px;\r\n\t\twidth: 